    private void ISBNButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ISBNButtonActionPerformed
        int[] isbn = new int[10];
        int index = 0;

        // convert input to string
        String s = jTextField1.getText();

        // loop ofver length of String 
        // check each char if int
        // if in add to the isbn array
        for (int i = 0; i < s.length(); i++) {
            if (Character.isDigit(s.charAt(i))) {
                isbn[index] = Integer.parseInt(String.valueOf(s.charAt(i)));
                index++;
            }
        }

        // check if entered number has length of 10
        //if it does it runs the check
        // else returns invalid input
        if (index == 10) {
            int sum = 0;
            for (int i = 0; i < 10; i++) {
                sum = sum + (isbn[i] * (i + 1));
            }

            sum = sum % 11;

            if (sum == 0) {
                jTextArea1.setText("Valid ISBN number");
            } else {
                jTextArea1.setText("Invalid ISBN number");
            }
        } else {
            jTextArea1.setText("Invalid input number");
        }
    }//GEN-LAST:event_ISBNButtonActionPerformed

