    private void cardButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cardButtonActionPerformed
        int[] card = new int[16];
        int index = 0;

        String s = jTextField2.getText();
        
        // loop ofver length of String 
        // check each char if int
        // if in add to the card array
        for (int i = 0; i < s.length(); i++) {
            if (Character.isDigit(s.charAt(i))) {
                card[index] = Integer.parseInt(String.valueOf(s.charAt(i)));
                index++;
            }
        }

         // check if entered number has length of 10
        //if it does it runs the check
        // else returns invalid input
        if (index == 16) {
        int sum = 0;
        for (int i = 0; i < 16; i++) {
            if ((i % 2) == 0) {
                card[i] = (card[i] * 2);
                if (card[i] >= 10) {
                    card[i] = card[i] - 9;
                }
            }
            sum = sum + card[i];
        }

        sum = sum % 10;

        if (sum == 0) {
            jTextArea1.setText("Valid card number");
        } else {
            jTextArea1.setText("Invalid card number");
        }
        }
        else {
            jTextArea1.setText("Invalid input number");
        }
    }//GEN-LAST:event_cardButtonActionPerformed

